package main

import "fmt"

func main() {
	tests := []struct {
		s1  string
		s2  string
		out bool
	}{
		{"ab", "eidbaooo", true},
		{"ab", "eidboaoo", false},
		{"a", "eidboaoo", true},
		{"aoo", "eidboaooo", true},
		{"ooo", "eidboaooo", true},
		{"ooob", "eidboaooo", false},
		{"q", "eidboaooo", false},
		{"aabb", "abbaaeidboaooo", true},
		{"adc", "dcda", true},
		{"adcd", "dcdbadcada", true},
		{"rokx", "otrxvfszxroxrzdsltg", false},
		{"rokxaaa", "zzzkoxaaxarok", true},
		{"", "", true},
	}
	for _, test := range tests {
		if got := checkInclusion(test.s1, test.s2); got != test.out {
			fmt.Printf("%+v, got: %v\n", test, got)
			panic("not ok")
		}
	}
	fmt.Println("ok")
}

func checkInclusion(s1 string, s2 string) bool {
	s2s := []byte(s2)
	s1b, s2b := [26]int{}, [26]int{}
	for i := range s1 {
		s1b[s1[i]-'a']++
	}
	last := 0
	for i, c := range s2s {
		s2b[c-'a']++
		for last <= i && s1b[s2s[last]-'a'] < s2b[s2s[last]-'a'] {
			s2b[s2s[last]-'a']--
			last++
		}
		if s1b == s2b {
			return true
		}
	}
	return false
}

func checkInclusion2(s1 string, s2 string) bool {
	s2b := []rune(s2)
	s1mapCount := make(map[rune]int, len(s1))
	countMap := 0
	for _, c := range s1 {
		s1mapCount[c]++
	}

	for i, c := range s2b {
		if count, exist := s1mapCount[c]; exist {
			if count > 0 {
				s1mapCount[c]--
				countMap++
				if countMap == len(s1) {
					return true
				}
			} else {
				last := 0
				for j := countMap; j > 0; j-- {
					last = i - j
					if s2b[last] == c {
						break
					}
					s1mapCount[s2b[last]]++
				}
				countMap = i - last
			}
			continue
		}
		if countMap != 0 {
			s1mapCount = make(map[rune]int, len(s1))
			for _, c := range s1 {
				s1mapCount[c]++
			}
			countMap = 0
		}
	}
	return false
}
